import {Component} from '@angular/core';
import {FormControl, FormGroup} from '@angular/forms';

export enum Menu {
  BOOKS = 'BOOKS',
  USERS = 'USERS',
  BORROWINGS = 'BORROWINGS',

  GENRE = 'GENRES'
}

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']

})


export class AppComponent {
    menu = Menu;
  actualMenu: Menu = Menu.USERS;
  form: FormGroup;

  formBooks: FormGroup;
  formGenres: FormGroup;
  formBorrows: FormGroup;
  persons: Array<any> = [];
  books: Array<any> = [];
  genres: Array<any> = [];
  borrows: Array<any> = [];


  constructor() {
    this.form = new FormGroup({
      name: new FormControl(),
      surname: new FormControl()
    })
    this.formBooks = new FormGroup({
      autor: new FormControl(),
      nazovKnihy: new FormControl()
    })
    this.formGenres = new FormGroup({
      zaner: new FormControl()
    })
    this.formBorrows = new FormGroup({
      name: new FormControl(),
      surname: new FormControl(),
      nazovKnihy: new FormControl()
    })
  }
  changeMenu(menuItem: Menu): void {
    this.actualMenu = menuItem;
  }

  savePerson(): void {
    this.persons.push(
      this.form.value);
    this.form.reset();
  }
  saveBooks(): void {
    this.books.push(
      this.formBooks.value);
    this.formBooks.reset();
  }
  saveGenres(): void {
    this.genres.push(
      this.formGenres.value);
    this.formGenres.reset();
  }

}









<div class="menu">
  <button (click)="changeMenu(menu.USERS)">POUŽÍVATELIA</button>
  <button (click)="changeMenu(menu.BOOKS)">KNIHY</button>
  <button (click)="changeMenu(menu.BORROWINGS)">VÝPOŽIČKY</button>
  <button (click)="changeMenu(menu.GENRE)">Zaner</button>
</div>

<ng-container *ngIf="actualMenu === menu.USERS">
  <h3>POUŽÍVATELIA</h3>
  <form [formGroup]="form" (submit)="savePerson()">
  <label>Meno:
    <input type="text"
           formControlName="name" />
  </label>
  <label>Priezvisko:
    <input type="text"
           formControlName="surname" />
  </label>
  <button type="button" (click) = "savePerson()">Ulozit</button>
</form>

<table *ngIf="persons.length">
  <tr>
    <th>Meno</th>
    <th>Priezvisko</th>
  </tr>
  <tr *ngFor="let person of persons">
    <td>{{ person.name }}</td>
    <td>{{ person.surname }}</td>
  </tr>
</table>
</ng-container>
<ng-container *ngIf="actualMenu === menu.BOOKS">
  <h3>KNIHY</h3>
  <form [formGroup]="formBooks" (submit)="saveBooks()">
    <label>Autor:
      <input type="text"
             formControlName="autor" />
    </label>
    <label>Nazov Knihy:
      <input type="text"
             formControlName="nazovKnihy" />
    </label>
    <button type="button" (click) = "saveBooks()">Ulozit</button>
  </form>

  <table *ngIf="books.length">
    <tr>
      <th>Autor</th>
      <th>Nazov Knihy</th>
    </tr>
    <tr *ngFor="let book of books">
      <td>{{ book.autor }}</td>
      <td>{{ book.nazovKnihy }}</td>
    </tr>
  </table>
</ng-container>

<ng-container *ngIf="actualMenu === menu.GENRE">
  <h3>VÝPOŽIČKY</h3>
  <form [formGroup]="formGenres" (submit)="saveGenres()">
    <label>Autor:
      <input type="text"
             formControlName="autor" />
    </label>
    <label>Nazov Knihy:
      <input type="text"
             formControlName="nazovKnihy" />
    </label>
    <button type="button" (click) = "saveGenres()">Ulozit</button>
  </form>

  <table *ngIf="genres.length">
    <tr>
      <th>Meno</th>
      <th>Priezvisko</th>
      <th>Nazov Knihy</th>
    </tr>
    <tr *ngFor="let genre of genres">
      <td>{{ genre.autor }}</td>
      <td>{{ genre.nazovKnihy }}</td>
    </tr>
  </table>
</ng-container>

<ng-container *ngIf="actualMenu === menu.GENRE">
  <h3>Zanre</h3>
</ng-container>
