import {Component} from '@angular/core';
import {FormControl, FormGroup}
  from '@angular/forms';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {
  cislo = "0";
  changeChecks(cislo: String): void {
    let num = Number(cislo);
    let bin = "";

    while (num != 0) {
      if (num % 2 == 1) {
        bin = "1" + bin;
      } else {
        bin = "0" + bin;
      }
      num = Math.floor(num / 2);
    }
    for (let a = 0 + bin.length; a < 8; a++) {
      bin = "0" + bin;
    }
    let b = [];
    for (let a = 0; a < bin.length; a++) {
      b[a] = bin[a];
    }
    for (let i = 0; i <= 7; i++) {
      let checkbox = document.getElementById(
        i.toString()
      ) as HTMLInputElement | null;

      if (checkbox != null) {
        if (b[i] == '1') {
          checkbox.checked = true;
        } else {
          checkbox.checked = false;
        }
      }
    }
  }

  changeInputNumber(): void {
    let dec = "";
    for (let i = 0; i <= 7; i++) {
      let checkbox = document.getElementById(
        i.toString()
      ) as HTMLInputElement | null;

      if (checkbox != null) {
        if (checkbox.checked == true) {
          dec = dec.concat("1")
        } else {
          dec = dec.concat("0")
        }
      }
    }
    let x = parseInt(dec, 2);
    this.cislo = x.toString();
  }

  form: FormGroup;
  persons: Array<any> = [];




  constructor() {
    this.form = new FormGroup({
      name: new FormControl(),
      surname: new FormControl()
    })
  }

  savePerson(): void{
    this.persons.push(this.form.value);
  }

}







<div style="margin-left: 45%">
  <input #inputNumber type="text"
  (input) = "changeChecks(inputNumber.value)" [value] = "cislo">
</div>
<div style="margin-left: 45.7%">
  <input type="checkbox" name="check1" id="0" (click) = "changeInputNumber()">
  <input type="checkbox" name="check1" id="1" (click) = "changeInputNumber()">
  <input type="checkbox" name="check1" id="2" (click) = "changeInputNumber()">
  <input type="checkbox" name="check1" id="3" (click) = "changeInputNumber()">
  <input type="checkbox" name="check1" id="4" (click) = "changeInputNumber()">
  <input type="checkbox" name="check1" id="5" (click) = "changeInputNumber()">
  <input type="checkbox" name="check1" id="6" (click) = "changeInputNumber()">
  <input type="checkbox" name="check1" id="7" (click) = "changeInputNumber()">


</div>


<form [formGroup]="form">
  <label>Meno:
    <input type="text"
           formControlName="name" />
  </label>
  <label>Priezvisko:
    <input type="text"
           formControlName="surname" />
  </label>
  <button type="button" (click) = "savePerson()">Ulozit</button>
</form>
